import React, { Component, Fragment } from 'react';

// importamos el mutation para poder conectarlo con el backend
import { NUEVO_CLIENTE_MUTATION } from '../mutations';
import { Mutation } from 'react-apollo';

export class NuevoCliente extends Component {
	state = {
		cliente: {
			nombre: '',
			apellido: '',
			empresa: '',
			edad: '',
			email: '',
			tipo: ''
		}
	};

	render() {
		return (
			<Fragment>
				<h2 className="text-center">Nuevo Cliente</h2>
				<div className="row justify-content-center">
					{/* ponemos aqui el mutation del formulario */}
					<Mutation mutation={NUEVO_CLIENTE_MUTATION}>
						{(crearCliente) => (
							<form
								className="col-md-8 m-3"
								onSubmit={(e) => {
									e.preventDefault();
									// esto lo traemos del state
									const { nombre, apellido, empresa, edad, tipo, email } = this.state.cliente;
									const input = {
										//nombre: nombre ->el primero es de lo que espera el mutation y el segundo es lo que se le da pero con las
										//ultimas caracteristicas de javascript, si lo que espera  lo que se ingresa es lo mismo basta con poner un solo nombre
										nombre,
										apellido,
										empresa,
										edad: Number(edad), //como edad en graphql es un int, no podemos pasarlo como si fuera un string. Usamos number para convertirlo y mandarlo a graphql sin error
										tipo,
										email
									};
									console.log(input);
								}}
							>
								<div className="form-row">
									<div className="form-group col-md-6">
										<label>Nombre</label>
										<input
											type="text"
											className="form-control"
											placeholder="Nombre"
											onChange={(e) => {
												this.setState({
													cliente: {
														// para que no me quite otras propiedades es con el state operator
														//así el resto del state no se ve afectado
														...this.state.cliente,
														nombre: e.target.value
													}
												});
											}}
										/>
									</div>
									<div className="form-group col-md-6">
										<label>Apellido</label>
										<input
											type="text"
											className="form-control"
											placeholder="Apellido"
											onChange={(e) => {
												this.setState({
													cliente: {
														...this.state.cliente,
														apellido: e.target.value
													}
												});
											}}
										/>
									</div>
								</div>
								<div className="form-row">
									<div className="form-group col-md-6">
										<label>Empresa</label>
										<input
											type="text"
											className="form-control"
											placeholder="Empresa"
											onChange={(e) => {
												this.setState({
													cliente: {
														...this.state.cliente,
														empresa: e.target.value
													}
												});
											}}
										/>
									</div>
									<div className="form-group col-md-6">
										<label>Email</label>
										<input
											type="email"
											className="form-control"
											placeholder="Email"
											onChange={(e) => {
												this.setState({
													cliente: {
														...this.state.cliente,
														email: e.target.value
													}
												});
											}}
										/>
									</div>
								</div>
								<div className="form-row">
									<div className="form-group col-md-6">
										<label>Edad</label>
										<input
											type="text"
											className="form-control"
											placeholder="Edad"
											onChange={(e) => {
												this.setState({
													cliente: {
														...this.state.cliente,
														edad: e.target.value
													}
												});
											}}
										/>
									</div>
									<div className="form-group col-md-6">
										<label>Tipo Cliente</label>
										<select
											onChange={(e) => {
												this.setState({
													cliente: {
														...this.state.cliente,
														tipo: e.target.value
													}
												});
											}}
											className="form-control"
										>
											<option value="">Elegir...</option>
											<option value="PREMIUM">PREMIUM</option>
											<option value="BASICO">BÁSICO</option>
										</select>
									</div>
								</div>
								<button type="submit" className="btn btn-success float-right">
									Guardar Cambios
								</button>
							</form>
						)}
					</Mutation>
				</div>
			</Fragment>
		);
	}
}

export default NuevoCliente;
