import React, { Component } from 'react';
import { ACTUALIZAR_CLIENTE_MUTATION } from '../mutations';
import { Mutation } from 'react-apollo';
import { withRouter } from 'react-router-dom';

export class FormularioEditarCliente extends Component {
	state = {
		cliente: this.props.cliente,
		emails: this.props.cliente.emails
	};

	constructor(props) {
		super(props);
		this.handleClick = this.handleClick.bind(this);
		selectedTab = 0;
	}

	handleClick(value) {
		selectedTab = value;
		// console.log(selectedTab);
		this.props.history.push('/Bldgs');
		// console.log(this.props);
	}

	nuevoCampo = () => {
		this.setState({
			emails: this.state.emails.concat([ { cuentaEmail: '' } ])
		});
	};

	leerCampo = (i) => (e) => {
		const nuevoMail = this.state.emails.map((cuentaEmail, index) => {
			if (i !== index) return cuentaEmail;
			return { ...cuentaEmail, cuentaEmail: e.target.value };
		});
		this.setState({ emails: nuevoMail });
	};

	quitarCampo = (i) => () => {
		this.setState({
			emails: this.state.emails.filter((s, index) => i !== index)
		});
	};

	render() {
		const { id, nombre, apellido, empresa, edad, tipo } = this.state.cliente;
		// error gordo
		const { emails } = this.state;

		return (
			<Mutation
				mutation={ACTUALIZAR_CLIENTE_MUTATION}
				onCompleted={() =>
					this.props.refetch().then(() => {
						this.props.history.push('/');
					})}
			>
				{(actualizarCliente) => (
					<form
						className="col-md-8 m-3"
						onSubmit={(e) => {
							e.preventDefault();
							const { nombre, apellido, empresa, edad, tipo } = this.state.cliente;
							const { emails } = this.state;
							const input = {
								id,
								nombre,
								apellido,
								empresa,
								edad: Number(edad),
								tipo,
								emails
							};
							//console.log(input);
							actualizarCliente({
								variables: { input }
							});
						}}
					>
						<div className="form-row">
							<div className="form-group col-md-6">
								<label>Nombre</label>
								<input
									type="text"
									className="form-control"
									defaultValue={nombre}
									onChange={(e) => {
										this.setState({
											cliente: {
												...this.state.cliente,
												nombre: e.target.value
											}
										});
									}}
								/>
							</div>
							<div className="form-group col-md-6">
								<label>Apellido</label>
								<input
									type="text"
									className="form-control"
									defaultValue={apellido}
									onChange={(e) => {
										this.setState({
											cliente: {
												...this.state.cliente,
												apellido: e.target.value
											}
										});
									}}
								/>
							</div>
						</div>

						<div className="form-row">
							<div className="form-group col-md-12">
								<label>Empresa</label>
								<input
									type="text"
									className="form-control"
									defaultValue={empresa}
									onChange={(e) => {
										this.setState({
											cliente: {
												...this.state.cliente,
												empresa: e.target.value
											}
										});
									}}
								/>
							</div>

							{emails.map((input, index) => (
								<div key={index} className="form-group col-md-12">
									<label>Email {index + 1} : </label>
									<div className="input-group">
										<input
											type="email"
											placeholder={`Email`}
											className="form-control"
											onChange={this.leerCampo(index)}
											defaultValue={input.cuentaEmail}
										/>
										<div className="input-group-append">
											<button
												className="btn btn-danger"
												type="button"
												onClick={this.quitarCampo(index)}
											>
												&times; Eliminar
											</button>
										</div>
									</div>
								</div>
							))}
							<div className="form-group d-flex justify-content-center col-md-12">
								<button onClick={this.nuevoCampo} type="button" className="btn btn-warning">
									+ Agregar Email
								</button>
							</div>
						</div>
						<div className="form-row">
							<div className="form-group col-md-6">
								<label>Edad</label>
								<input
									type="text"
									className="form-control"
									defaultValue={edad}
									onChange={(e) => {
										this.setState({
											cliente: {
												...this.state.cliente,
												edad: e.target.value
											}
										});
									}}
								/>
							</div>
							<div className="form-group col-md-6">
								<label>Tipo Cliente</label>
								<select
									className="form-control"
									value={tipo}
									onChange={(e) => {
										this.setState({
											cliente: {
												...this.state.cliente,
												tipo: e.target.value
											}
										});
									}}
								>
									<option value="">Elegir...</option>
									<option value="PREMIUM">PREMIUM</option>
									<option value="BASICO">B√ÅSICO</option>
								</select>
							</div>
						</div>
						<button type="submit" className="btn btn-success float-right">
							Guardar Cambios
						</button>
					</form>
				)}
			</Mutation>
		);
	}
}

export default withRouter(FormularioEditarCliente);
